class Solution {
    // Time O(log(n))
    public int search(int[] nums, int target) {
        int left = 0;
        int right = nums.length-1;
        int mid = 0;
        while (left <= right) { // need <= in case array is size 1
            mid = left + (right-left) / 2;
            if (nums[mid] == target) {
                return mid;
            } else if (target > nums[mid]) {
                left = mid+1; // change left bound
            }
            else if(target < nums[mid]) {
                right = mid-1; // change right bound
            }
        }

        return -1;
    }
}
