        for(int i = 0; i < tokens.Length; i++){
            switch(tokens[i]){
                case ".":
                    break;
                case "..":
                    if(answerList.Count > 0) answerList.RemoveLast();
                    break;
                default:
                    answerList.AddLast(tokens[i]);
                    break;
            }
        }

        return "/" + string.Join('/', answerList);
