        }
        //=============================================================================================
        string ans = "";
        while(!st.empty()) 
        { 
            ans = "/" + st.top() + ans; 
            st.pop(); 
        }
        //==============================================================================================
        return (ans == "" ? "/" : ans);

    }
};
