class Solution {
public:
	string simplifyPath(string path) {
		string canonicalPath = ""; //for creating the final path / answer
		string curr = ""; //to build the current directory
		stack<string> s; //for implementing ".." -> POP feature (to go to the directory up a level)
		path += "/"; //(**IMP) we add this so that the last directory gets added in case the input doesn't end with "/"
		//example for the above case - "/a//b////c/d//././/.."
		for(int i=0;i<path.length();i++){
			if(path[i] == '/'){ //we have some cases when we find "/" 
				if(curr == ".."){ //here we have to implement pop to to the directory up a level
					if(!s.empty()) s.pop(); //check if the stack is already empty , in that case no need to do anything
				} else if(curr != "." and curr!= ""){ //we do nothing if curr directory which we built is "." or is empty
					s.push(curr); //push the directory into the stack
				}
				curr = ""; //resetting the curr string after a directory is successfully added into the stack in order to build a new directory
			} else { //keep building the current string / directory
				curr += path[i]; //add the character to build the current directory
			}
		}
		//by here : - Example : IP - "/home//foo/" 
		//our stack now contains - |foo | or "fool","home" top to down 
		//                         |home|
		//now we have to start building our final answer , i.e string canonicalPath
		while(!s.empty()){
			canonicalPath = "/"+s.top()+canonicalPath;
			s.pop();
		}
		//incase our final answer is empty, then we add a condition to return "/" as the path always starts with a single slash "/"
		if(canonicalPath.length() == 0){
			return "/";
		} else { //return our final answer
			return canonicalPath;
		}
	}
};

