        dp, ans, dp_pre = [0] * len(matrix[0]), 0, 0
        for i in range(len(matrix)):
            for j in range(len(matrix[0])):
                if matrix[i][j] == '1':
                    if i == 0 or j == 0: 
                        dp[j], dp_pre = 1, dp[j]
                    else:
