def maximalSquare1(self, matrix):
    if not matrix:
        return 0
    r, c = len(matrix), len(matrix[0])
    dp = [[0 for i in xrange(c+1)] for j in xrange(r+1)]
    res = 0
    for i in xrange(r):
        for j in xrange(c):
            dp[i+1][j+1] = (min(dp[i][j], dp[i+1][j], dp[i][j+1])+1)*int(matrix[i][j])
            res = max(res, dp[i+1][j+1]**2)
    return res

