def maximalSquare2(self, matrix):
    if not matrix:
        return 0
    r, c = len(matrix), len(matrix[0])
    dp = [[int(matrix[i][j]) for j in xrange(c)] for i in xrange(r)]
    res = max(max(dp))
    for i in xrange(1, r):
