class Solution {
    public int maximalSquare(char[][] matrix) {
        if (matrix.length == 0 || matrix[0].length == 0) return 0;
        int n = matrix.length;
        int m = matrix[0].length;
        int max = 0;
        Integer[][] cache = new Integer[n][m];
        for(int i = 0; i < n; i++) {
            for(int j = 0; j < m; j++) {
                max = Math.max(max, helper(matrix, i, j, cache));
            }
        }

        return max * max;
    }

    private int helper(char[][] matrix, int i, int j, Integer[][] cache) {
        if (i == matrix.length || j == matrix[0].length) return 0;
        if (matrix[i][j] == '0') return 0;
        if (cache[i][j] != null) return cache[i][j];

        int re1 = helper(matrix, i, j + 1, cache);
        int re2 = helper(matrix, i + 1, j, cache);
        int re3 = helper(matrix, i + 1, j + 1, cache);

        cache[i][j] = Math.min(re1, Math.min(re2,re3)) + (matrix[i][j] - '0');
        return cache[i][j];
    }
}
