        dp_table = matrix

        max_edge_length = 0

        for x in range(w):
            matrix[0][x] = int( matrix[0][x] )

        for y in range(h):
            matrix[y][0] = int( matrix[y][0] )

        for y in range(h):
            for x in range(w):

                if y > 0 and x > 0:
                    if matrix[y][x] == '1':

                        matrix[y][x] = 1 + min( matrix[y][x-1], matrix[y-1][x-1], matrix[y-1][x])

                    else:
                        matrix[y][x] = 0

                max_edge_length = max(max_edge_length, matrix[y][x])

        return max_edge_length*max_edge_length

