public class Solution {

    public int maximalSquare(char[][] matrix) {
        int m = matrix.length;
        if(m <= 0)return 0;
        int n = matrix[0].length;
        int[][] dp = new int[m][n];
        for(int i = 0; i < m; ++i)
            dp[i][0] = matrix[i][0] - '0';
        for(int j = 0; j < n; ++j)
            dp[0][j] = matrix[0][j] - '0';
        for(int i = 1; i < m; ++i)
            for(int j = 1; j < n; ++j){
                if(matrix[i][j] == '0'){
                    dp[i][j] = 0;
                }else{
                    int L1 = dp[i][j - 1];
                    int L2 = dp[i - 1][j];
                    if(L1 != L2)
                        dp[i][j] = Math.min(L1, L2) + 1;
                    else
                        dp[i][j] = (matrix[i-L1][j-L1] == '1') ? L1 + 1 : L1;            
                }
            }
        int max = 0;
        for(int[] row : dp)
            for(int col : row)
                max = Math.max(max, col);
        return max * max;
    }
}

