def maximalSquare(matrix):
    if not matrix:
        return 0
    m, n, max_len, cache = len(matrix), len(matrix[0]), 0, 0
    dp = [0] * (n+1)
    for i in range(m):
        for j in range(n):
            if matrix[i][j] == "1":
                dp[j+1], cache = min(dp[j], dp[j+1], cache) + 1, dp[j+1]
                max_len = max(max_len, dp[j+1])
            else:
                dp[j+1], cache = 0, dp[j+1]
    return max_len * max_len
