class Solution {
    public int maximalSquare(char[][] matrix) {
        int[][] map = new int[matrix.length][matrix[0].length];
        int result = 0;

        for (int i = 0; i < matrix.length; i++) {
            for (int j = 0; j < matrix[0].length; j++) {
                result = Math.max(result, helper(i, j, matrix, map));
            }
        }
        return result * result;
    }

    public int helper(int r, int c, char[][] arr, int[][] map) {
        if (r == arr.length || c == arr[0].length || arr[r][c] == '0')
            return 0;

        if (map[r][c] != 0)
            return map[r][c];

        int current = Math.min(helper(r + 1, c, arr, map),
                Math.min(helper(r, c + 1, arr, map), helper(r + 1, c + 1, arr, map)));

        map[r][c] = current + 1;
        return map[r][c];
    }
}
