    pair<int,int> setval(vector<vector<pair<int,int>>>& dp, int i, int j, int v)
    {
        if (i==0 or j==0 or v==0)
        {
            area = max(area, v);
            return {v,v};
        }
        int x = min(dp[i-1][j].second, dp[i][j-1].second);
        int k = sqrt(dp[i-1][j-1].first);
        if(k<=x)
        {
            x++;
            k++;
            area = max(k*k, area);
            return {k*k, x};
        }
        x++;
        area = max(x*x, area);
        return {x*x, x};
    }

    int maximalSquare(vector<vector<char>>& matrix) {
        int n = matrix.size(), m = matrix[0].size();
        vector<vector<pair<int,int>>> dp(n,vector<pair<int,int>>(m, {0,0}));

        for(int i=0;i<n;i++)
        {
            for(int j=0;j<m;j++)
            {
                dp[i][j] = setval(dp, i, j, matrix[i][j]-'0');
            }
        }
        return area;
    }
};
