	// O(col)
	public int largestSquareArea(int[] heights) {
		int max = 0, len = heights.length;

		int[] stack = new int[len];

		int i = 0, idx = -1;

		while (i < len) {

			if (idx == -1 || heights[stack[idx]] <= heights[i]) {
				idx++;
				stack[idx] = i;
				i++;
			} else {

				int pb = heights[stack[idx]];
                idx--;

				int r = i;
				int l = -1;

				if (idx != -1)
					l = stack[idx];

				int min = Math.min(r - l - 1, pb);
				int area = min * min;

				if (area > max)
					max = area;
			}
		}

		while (idx != -1) {

			int pb = heights[stack[idx]];
            idx--;

			int r = len;
			int l = -1;

			if (idx != -1)
				l = stack[idx];

			int min = Math.min(r - l - 1, pb);
			int area = min * min;

			if (area > max)
				max = area;
		}

		return max;
	}

