class Solution {
public:
    int dp[301][301];
    int dfs(vector<vector<char>> &mat, int i, int j) {

        if(i>=mat.size() || j>=mat[0].size() || mat[i][j]=='0') return 0;

        if(dp[i][j]!=-1) return dp[i][j];

        return dp[i][j] = min({
            dfs(mat,i+1,j)+1,
            dfs(mat,i,j+1)+1,
            dfs(mat,i+1,j+1)+1
        });
