        if (matrix == null || matrix.Length == 0)
            return 0;

        int m = matrix.Length, n = matrix[0].Length;

        //dp[i,j]: max length of a square with right-bottom at [i,j]
        int[,] dp = new int[m, n];
        int max = 0;

        for (int i = 0; i < m; i++)
        {
            for (int j = 0; j < n; j++)
            {
                if (matrix[i][j] == '0')
                    continue;

                if (i == 0 || j == 0)
                    dp[i, j] = 1;
                else
                    dp[i, j] = Math.Min(dp[i - 1, j - 1], Math.Min(dp[i - 1, j], dp[i, j - 1])) + 1;

                max = Math.Max(max, dp[i, j]);
            }
        }

        return max * max;
    }
}
