class Solution {
    fun maximalSquare(A: Array<CharArray>): Int {
        var best = 0
        var (M, N) = listOf(A.size, A[0].size)
        fun go(i: Int, j: Int): Int {
            if (A[i][j] == '0' || i == M - 1 || j == N - 1)
                return if (A[i][j] == '1') 1 else 0
            var D = go(i + 1, j)
            var R = go(i, j + 1)
            var X = go(i + 1, j + 1)
            return 1 + listOf(D, R, X).min()!!
        }
        for (i in 0 until M)
            for (j in 0 until N)
                best = Math.max(best, go(i, j))
        return best * best
    }
}
