class Solution {
    fun maximalSquare(A: Array<CharArray>): Int {
        var best = 0
        var (M, N) = listOf(A.size, A[0].size)
        var dp = Array(M) { IntArray(N) { 0 } }
        for (i in 0 until M) if (A[i][0] == '1') { dp[i][0] = 1; best = 1 }
        for (j in 0 until N) if (A[0][j] == '1') { dp[0][j] = 1; best = 1 }
        for (i in 1 until M) {
            for (j in 1 until N) {
                if (A[i][j] == '0')
                    continue
                var U = dp[i - 1][j]
                var L = dp[i][j - 1]
                var X = dp[i - 1][j - 1]
                dp[i][j] = 1 + listOf(U, L, X).min()!!
                best = Math.max(best, dp[i][j])
            }
        }
        return best * best
