4. Memory optimization from O(N<sup>2</sup>) to O(N) by replacing entire `dp` matrix with previous `pre` and current `cur` rows
```
class Solution {
    fun maximalSquare(A: Array<CharArray>): Int {
        var best = 0
        var (M, N) = listOf(A.size, A[0].size)
        var pre = IntArray(N) { 0 }
        for (j in 0 until N)
            if (A[0][j] == '1') { pre[j] = 1; best = Math.max(best, 1) }
        for (i in 1 until M) {
            var cur = IntArray(N) { 0 }
            if (A[i][0] == '1') { cur[0] = 1; best = Math.max(best, 1) }
            for (j in 1 until N) {
                if (A[i][j] == '0')
                    continue
                var U = pre[j]
                var L = cur[j - 1]
                var X = pre[j - 1]
                cur[j] = 1 + listOf(U, L, X).min()!!
                best = Math.max(best, cur[j])
            }
            pre = cur.also{ cur = pre }
        }
        return best * best
    }
}
