class Solution {
public:
    using VC = vector<char>;
    using VVC = vector<VC>;
    using fun = function<int(int, int)>;
    int maximalSquare(VVC& A, int best = 0) {
        int M = A.size(),
            N = A[0].size();
        fun go = [&](auto i, auto j) {
            if (A[i][j] == '0' || i == M - 1 || j == N - 1)
                return A[i][j] - '0';
            auto D = go(i + 1, j),
                 R = go(i, j + 1),
                 X = go(i + 1, j + 1);
            return 1 + min({ D, R, X });
        };
        for (auto i{ 0 }; i < M; ++i)
            for (auto j{ 0 }; j < N; ++j)
                best = max(best, go(i, j));
        return best * best;
    }
};
