class Solution {
public:
    using VC = vector<char>;
    using VVC = vector<VC>;
    using VI = vector<int>;
    using VVI = vector<VI>;
    int maximalSquare(VVC& A, int best = 0) {
        int M = A.size(),
            N = A[0].size();
        VI pre(N);
        for (auto j{ 0 }; j < N; ++j)
            pre[j] = A[0][j] == '1', best = max(best, pre[j]);
        for (auto i{ 1 }; i < M; ++i) {
            VI cur(N);
            cur[0] = A[i][0] == '1', best = max(best, cur[0]);
            for (auto j{ 1 }; j < N; ++j) {
                if (A[i][j] == '0')
                    continue;
                auto U = pre[j],
                     L = cur[j - 1],
                     X = pre[j - 1];
                cur[j] = 1 + min({ U, L, X });
                best = max(best, cur[j]);
            }
            swap(pre, cur);
        }
        return best * best;
    }
};
