        cur[0] = Number(A[i][0] == '1'), best = Math.max(best, cur[0]);
        for (let j = 1; j < N; ++j) {
            if (A[i][j] == '0')
                continue;
            let U = pre[j],
                L = cur[j - 1],
                X = pre[j - 1];
            cur[j] = 1 + Math.min(U, L, X);
            best = Math.max(best, cur[j]);
        }
        [pre, cur] = [cur, pre];
    }
    return best * best;
