            VI cur(N);
            cur[0] = A[i][0] == '1', best = max(best, cur[0]);
            for (auto j{ 1 }; j < N; ++j) {
                if (A[i][j] == '0')
                    continue;
                auto U = pre[j],
                     L = cur[j - 1],
                     X = pre[j - 1];
                cur[j] = 1 + min({ U, L, X });
                best = max(best, cur[j]);
            }
            swap(pre, cur);
        }
        return best * best;
    }
