class Solution 
{
    public int maximalSquare(char[][] matrix) 
    {

        int m = matrix.length;
        if (m == 0) return 0;
        int n = matrix[0].length;
        if (n == 0) return 0;

        // brute force
        int max_k = -1;
        for (int i = 0; i < m; ++i)
        {
            for (int j = 0; j < n; ++j)
            {
                for (int k = 0; i + k < m && j + k < n; ++k)
                {
                    if (only_ones(matrix, i, j, k))
                    {
                        max_k = Math.max(k, max_k);
                    }
                    else
                    {
                        break;
                    }
                }
            }
        }

        ++max_k;
        return max_k * max_k;
    }

    boolean only_ones(char[][] G, int start_i, int start_j, int k)
    {
        for (int i = start_i; i <= start_i + k; ++i)
        {
            for (int j = start_j; j <= start_j + k; ++j)
            {
                if (G[i][j] != '1') return false;
            }
        }

        return true;
    }
}
