class Solution {
    public int maximalSquare(char[][] matrix) {
        if (matrix == null || matrix.length == 0 || matrix[0].length == 0) {
            return 0;
        }

        // Brute force
        int ans = 0;

        int nr = matrix.length;
        int nc = matrix[0].length;

        for (int len = 1; len <= Math.min(nr, nc); len++) {
            for (int i = 0; i + len <= nr; i++) {
                for (int j = 0; j + len <= nc; j++) {
                    // Top-left point is (i, j), with len, check every square
                    if (isSquare(i, j, len, matrix)) {
                        ans = Math.max(ans, len * len);
                    }
                }
            }
        }

        return ans;
    }

    private boolean isSquare(int i, int j, int len, char[][] matrix) {
        for (int m = i; m < i + len; m++) {
            for (int n = j; n < j + len; n++) {
                if (matrix[m][n] == '0') {
                    return false;
                }
            }
        }
        return true;
    }
}
