class Solution {


int SolveByMemoization(vector<vector<char>> &matrix, int m, int n, int i, int j, int &maxi, vector<vector<int>> &dp)
{
    if (i == m || j == n)
        return 0;

    if (dp[i][j] != -1)
        return dp[i][j];

    int diagonal = SolveByMemoization(matrix, m, n, i + 1, j + 1, maxi, dp);
    int right = SolveByMemoization(matrix, m, n, i, j + 1, maxi, dp);
    int down = SolveByMemoization(matrix, m, n, i + 1, j, maxi, dp);

    if (matrix[i][j] == '1')
    {

        int ans = 1 + min(diagonal, min(right, down));

        maxi = max(maxi, ans);
        return dp[i][j] = ans;
    }

    return dp[i][j] = 0;
}

void SolveByTabulation(vector<vector<char>> &matrix, int m, int n, int &maxi)
{
    vector<vector<int>> dp(m+1, vector<int>(n+1, 0));


    for (int i = m - 1; i >= 0; i--)
    {
        for (int j = n - 1; j >= 0; j--)
        {
            if (matrix[i][j] == '0')
                dp[i][j] = 0;
            else
            {
                dp[i][j] = 1 + min(dp[i + 1][j + 1], min(dp[i + 1][j], dp[i][j + 1]));
                maxi = max(dp[i][j], maxi);
            }
        }
    }

}


