class Solution {
public:
    /*
    we need to check in these 3 direction to make our pre exisitng matrix largest 
      \    |
       \   |
        \  |
         \ |
    ______\|

    if there is a possibility that
    matrix[i-1][j-1] = 1
    matrix[i-1][j] = 1
    matrix[i][j-1] = 1 &
    matrix[i][j] = 1 then we can include that 1 and make our matrix larger , we loop over the matrix and keep the record of largest matrix we have encountered
    */
    int maximalSquare(vector<vector<char>>& matrix) {
        int rows = matrix.size();
        if(rows == 0) return 0;
        int cols = matrix[0].size();
        vector<vector<int>> dp(rows+1,vector<int>(cols+1,0));
        int largest = 0;
        for(int i=1;i<=rows;i++)
        {
            for(int j=1;j<=cols;j++)
            {
                if(matrix[i-1][j-1] == '1'){
                dp[i][j] = 1+min(dp[i-1][j],min(dp[i][j-1],dp[i-1][j-1]));
                if(largest < dp[i][j])
                {
                   largest = dp[i][j];
                }
              }
            }
        }
        return largest*largest;
    }
};
