int btup(vector<vector<char>>& matrix){
        int r = matrix.size() ;
        int c = matrix[0].size() ;
        vector<vector<int>> dp(r,vector<int>(c)) ;
        int ans = INT_MIN ;
        for(int i = 0;i<c;i++){
            dp[r-1][i] = matrix[r-1][i]-'0';
            ans = max(ans,dp[r-1][i]) ;
        }

        for(int i = 0;i<r;i++){
            dp[i][c-1] = matrix[i][c-1]-'0' ;
            ans = max(ans,dp[i][c-1]) ;
        }

        for(int i = r-2;i>=0;i--){
            for(int j = c-2;j>=0;j--){
               if(matrix[i][j] == '1'){
                    dp[i][j] = 1+ min(dp[i][j+1],min(dp[i+1][j+1],dp[i+1][j])) ;
               }
                ans = max(ans,dp[i][j]) ;
            }
        }
        return ans == INT_MIN? 0:ans*ans ;

		/*
		Time : O(r*c)
		Space : O(r*c) //no extra recursive stack space
		*/

    }
