class Solution {
public:
    int dp[301][301];
    int ans = 0;
    int solve(vector<vector<char>> &matrix, int i, int j, int m, int n)
    {
        if(i<0 || i>=m || j<0 || j>=n || matrix[i][j] == '0')
            return 0;
        if(dp[i][j] != -1)
            return dp[i][j];
        int down = solve(matrix, i+1, j, m, n);
        int right = solve(matrix, i, j+1, m, n);
        int diagonal = solve(matrix, i+1, j+1, m, n);
        return dp[i][j] = 1 + min(min(down, right), diagonal);

    }
    int maximalSquare(vector<vector<char>> &matrix) 
    {
        memset(dp, -1, sizeof(dp));
        for(int i=0; i<matrix.size(); i++)
            for(int j=0; j<matrix[0].size(); j++)
                if(matrix[i][j] == '1')
                    ans = max(ans, solve(matrix, i, j, matrix.size(), matrix[0].size()));
        return ans*ans;
    }
};
```

