    // The below code is the exact copy of: https://leetcode.com/problems/maximal-rectangle/

    //🚨NOTE🚨-->  The only difference is that, in histogram we will take the minimum of height and length as the side of square

    int maxAreaHist(vector<int>heights){

        int n = heights.size();

        vector<int> nse(n,n); NSE(nse,heights);

        vector<int> pse(n,-1); PSE(pse, heights);

        int maxi = 0;

        for(int i=0;i<n;i++){
            int length = nse[i] - pse[i] - 1;
            int height = heights[i];

            int sideOfSquare = min(length, height);//🚨🚨🚨🚨 THE ONLY DIFFERENCE! OTHERWISE EXACLY SAME 🚨🚨🚨🚨

            maxi = max(maxi,sideOfSquare*sideOfSquare );
        }


        return maxi;
    }


    int maximalSquare(vector<vector<char>>& matrix) {

        int maxiSquareArea = 0;

        int R = matrix.size(); if(R==0) return 0;
        int C = matrix[0].size();

        vector<int> heights (C, 0);

        for(int i = 0; i<R;i++){
            for(int j=0;j<C;j++){
                if(matrix[i][j]=='1')   heights[j]+=1;
                else heights[j] = 0;
            }

            maxiSquareArea = max(maxiSquareArea,maxAreaHist(heights) );
        }

        return maxiSquareArea;

    }


void NSE(vector<int>& nse, vector<int>& nums){
        int n = nums.size();

        deque<int> mini;

        for(int i=0;i<n;i++){

            while(mini.size() && nums[i] < nums[mini.back()])
            {
                nse[mini.back()] = i;
                mini.pop_back();
            }

            mini.push_back(i);
        }

        return; 
    }

void PSE(vector<int>& pse, vector<int>& nums){
        int n = nums.size();

        deque<int> mini;

        for(int i=n-1;i>-1;i--){

            while(mini.size() && nums[i] < nums[mini.back()])
            {
                pse[mini.back()] = i;
                mini.pop_back();
            }

            mini.push_back(i);
        }

        return; 
    }
};
