class Solution {
    public int maximalSquare(char[][] matrix) {
        int max = 0;
        int n = matrix.length;
        int m = matrix[0].length;
        int[][] dp = new int[n][m];

        for(int i = n - 1; i >= 0; i--) {
            for(int j = m - 1; j >= 0; j--) {
                if(i == n - 1 || j == m - 1) {
                    if(matrix[i][j] == '1') { 
                        dp[i][j] = 1;
                        max = Math.max(max, dp[i][j]);
                    }
                    continue;
                }

                else if(matrix[i][j] == '0') {
                    continue;
                }

                dp[i][j] = Math.min(dp[i + 1][j + 1], Math.min(dp[i + 1][j], dp[i][j + 1])) + 1;
                max = Math.max(max, dp[i][j]);
            }
        }
        return max * max;
    }
}
