        int dp[R][C];
        for(int i = 0;i<R;i++){
            for(int j = 0;j<C;j++)  dp[i][j] = 0;
        }
        bool ok = false;
        for(int i = 0;i<R;i++){
            if(m[i][0] == '1')  ok = true;
            dp[i][0] = (m[i][0] == '1' ? 1 : 0);
        }
        for(int j = 0;j<C;j++){
            if(m[0][j] == '1')  ok = true;
            dp[0][j] = (m[0][j] == '1' ? 1 : 0);
        }
        int res = ok;
        for(int i = 1;i<R;i++){
            for(int j = 1;j<C;j++){
                if(m[i][j] == '0')  dp[i][j] = 0;
                else{
                    dp[i][j] = min({dp[i - 1][j - 1], dp[i - 1][j], dp[i][j - 1]}) + 1;
                    res = max(res, dp[i][j]);
                }
            }
        }
        return res*res;
    }
};
