```
public class Solution {
    public int MaximalSquare(char[][] matrix) {
        int maxArea = int.MinValue;

        List<(int i, int j)> points = new List<(int, int)>();

        // Get every point
        for (int i = 0; i < matrix.Length; i++) {
            for (int j = 0; j < matrix[0].Length; j++) {                
                points.Add((i, j));
            }
        }

        // Every pair of points
        for (int i = 0; i < points.Count; i++) {
            for (int j = 0; j < points.Count; j++) {
                // If it's not an m x m square, don't check the area
                if (points[j].i - points[i].i != points[j].j - points[i].j) continue;

                maxArea = Math.Max(maxArea, CountArea(points[i], points[j], matrix));
            }
        }
        return maxArea;
    }

	// Return the area between 2 points in the matrix
    private int CountArea((int i, int j) begin, (int i, int j) end, char[][] matrix) {
        int count = 0;

        int x1 = begin.i;
        int y1 = begin.j;
        int x2 = end.i;
        int y2 = end.j;

        for (int i = x1; i <= x2; i++) {
            for (int j = y1; j <= y2; j++) {
                // Invalid square
                if (matrix[i][j] != '1') return 0;
                count++;
            }
        }

        return count;
    }  
}
