var maximalSquare = function(matrix) {
    if(!matrix.length) return 0;
    const R_NUM = matrix.length, C_NUM = matrix[0].length;
    const dp = Array.from({length: R_NUM+1}, () => Array(C_NUM+1).fill(0));
    let maxLen = 0;

