        int cols = matrix[0].length;
        int[][] dp = new int[rows][cols];
        for(int i=0; i<rows; i++){
            dp[i][0] = matrix[i][0]=='0'?0:1;
            max = Math.max(max, dp[i][0]);
        }
        for(int i=0; i<cols; i++){
            dp[0][i] = matrix[0][i]=='0'?0:1;
