   for(int i=0;i<row;i++) dp[i] = new int[cols];

   for(int j=0;j<cols;j++){
       dp[row-1][j] = matrix[row-1][j] -'0';
       max_indp  = max(max_indp,dp[row-1][j]);
   }
   for(int j=0;j<row;j++){
       dp[j][cols-1] = matrix[j][cols-1] -'0';
       max_indp  = max(max_indp,dp[j][cols-1]);
   }

   for(int i=row-2;i>=0;i--){
       for(int j=cols-2;j>=0;j--){

           int step1 = dp[i][j+1];
           int step2 = dp[i+1][j];
           int step3 = dp[i+1][j+1];

           int ans = min(step1,min(step2,step3));

           if(matrix[i][j] =='0') dp[i][j]=0;
           else{
               ans++;
               max_indp = max(max_indp,ans);
               dp[i][j]=ans;
           }
       }
   }
  return max_indp*max_indp;
}
};

