#define mp              make_pair
#define mt              make_tuple
#define pii             pair<int,int>
#define vi              vector<int>
#define mii             map<int,int>
#define FIO             ios_base::sync_with_stdio(0); cin.tie(0); cout.tie(0)
const int N = 1e5 + 10;

class Solution
{
public:
    map<int, list<int>> g;
    mii par;
    vi x_path;
    vi y_path;
    void dfs(int src, int parent)
    {
        par[src] = parent;
        for (auto nbr : g[src])
        {
            if (nbr != parent)
            {
                dfs(nbr, src);
            }
        }
    }
    vi findPath(int node)
    {
        vi path;
        int parent = par[node];
        path.pb(node);
        while (parent != -1)
        {
            path.pb(parent);
            parent = par[parent];
        }
        reverse(path.begin(), path.end());
        return path;
    }
    TreeNode *reqNode(TreeNode *root, int data)
    {
        if (root == NULL)
            return NULL;
        if (root->val == data)
            return root;
        TreeNode *l = reqNode(root->left, data);
        TreeNode *r = reqNode(root->right, data);
        return l == NULL ? r : l;
    }
    TreeNode *lowestCommonAncestor(TreeNode *root, TreeNode *x, TreeNode *y)
    {
        FIO;
        queue<TreeNode *> q;
        q.push(root);
        while (!q.empty())
        {
            auto f = q.front();
            q.pop();
            int u = f->val;
            if (f->left != NULL)
            {
                int v = f->left->val;
                g[u].push_back(v);
                q.push(f->left);
            }
            if (f->right != NULL)
            {
                int v = f->right->val;
                g[u].push_back(v);
                q.push(f->right);
            }
        }
        dfs(root->val, -1);
        x_path = findPath(x->val);
        y_path = findPath(y->val);
        int lca = -1;
        int mn = min(x_path.size(), y_path.size());
        for (int i = 0; i < mn; i++)
        {
            if (x_path[i] == y_path[i])
            {
                lca = x_path[i];
            }
            else
            {
                break;
            }
        }

        return reqNode(root, lca);
    }
};
