    public TreeNode lowestCommonAncestor(TreeNode root, TreeNode p, TreeNode q) {
        // Iterative approach
        TreeNode node = root;
        while (node != null) {
            if (p.val < node.val && q.val < node.val) {
                node = node.left;
            }
            else if (p.val > node.val && q.val > node.val) {
                node = node.right;
            }
            else {
                return node;
            }
        }
        return node;
    }
}
