                b[n-1]+minimum(a, b, m, n-1)
            );
        }
    }
}
 // top down
 int minimumTopDown(string a, string b, int m, int n, vector<vector<int>>& res){
    if(res[m][n] != -1){
        return res[m][n];
    } else if(m==0 && n==0){
        return res[m][n] = 0;
    } else if(m==0){
        return res[m][n] = (int)b[n-1] + minimumTopDown(a, b, m, n-1, res);
    } else if(n==0){
        return res[m][n] = (int)a[m-1] + minimumTopDown(a, b, m-1, n, res);
    } else {
        if(a[m-1]==b[n-1]){
            return res[m][n] = minimumTopDown(a, b, m-1, n-1, res);
        } else {
            return res[m][n] = min(
                a[m-1]+minimumTopDown(a, b, m-1, n, res),
                b[n-1]+minimumTopDown(a, b, m, n-1, res)    
            );
        }
    }
 }

// botttom up
int minimumBottomUp(string a, string b, int m, int n){
    int res[m+1][n+1];
    for(int i=0; i<=m; i++){
        for(int j=0; j<=n; j++){
            if(i==0 && j==0){
                res[i][j] = 0;
            } else if(i<=0 && j>=0){
                res[i][j] = (int)b[j-1] + res[i][j-1];
            } else if(j<=0 && i>0) {
                res[i][j] = (int)a[i-1] + res[i-1][j];
            } else {
                if(a[i-1]==b[j-1]){
                    res[i][j] = res[i-1][j-1];
                } else {
                    res[i][j] = min(a[i-1]+res[i-1][j], b[j-1]+res[i][j-1]);
                }
            }
        }
    }

    return res[m][n];
}

