    {
        vector<vector<int>>  dp(s.size()+1,vector<int>(t.size()+1,INT_MAX));
        dp[0][0]=0;
        for(int i=1;i<t.size()+1;i++)
            dp[0][i]=dp[0][i-1]+t[i-1];
        for(int i=1;i<s.size()+1;i++)
            dp[i][0]=dp[i-1][0]+s[i-1];
        for(int i=1;i<s.size()+1;i++)
        {
            for(int j=1;j<t.size()+1;j++)
            {
                if(s[i-1]==t[j-1])
                {
                    dp[i][j]=dp[i-1][j-1];
                }
                else
                {
                        dp[i][j]=min({dp[i][j-1]+t[j-1],dp[i-1][j]+s[i-1]}); 
                }
            }
        }
        return dp.back().back();
    }
};
