class Solution:
    def minimumDeleteSum(self, s1: str, s2: str) -> int:
        n=len(s1)
        m=len(s2)
        dp=[[0 for x in range(m+1)] for x in range(n+1)]
        for i in range(1,n+1):
            dp[i][0]=dp[i-1][0]+ord(s1[i-1])

