    int minimumDeleteSum(string s1, string s2) {
        const int len1 = s1.size(), len2 = s2.size();
        if (len1 < len2) return minimumDeleteSum(s2, s1);
        vector<int> dp0(len2 + 1), dp1(len2 + 1);

        // base case
        dp1[len2] = 0;
        for (int i2 = len2 - 1; i2 >= 0; i2--) dp1[i2] = dp1[i2 + 1] + s2[i2];

        // dynamic programming
        for (int i1 = len1 - 1; i1 >= 0; i1--) {
            dp0[len2] = dp1[len2] + s1[i1];
            for (int i2 = len2 - 1; i2 >= 0; i2--) {
                if (s1[i1] == s2[i2]) dp0[i2] = dp1[i2 + 1];
                else dp0[i2] = min(s1[i1] + dp1[i2], s2[i2] + dp0[i2 + 1]);
            }
            swap(dp0, dp1);
        }

        return dp1[0];
    }
};
