        {
            dp[i][0] = dp[i - 1][0] + s1[i-1];
        }

        for (int i = 1; i <= m; i++)
        {
            dp[0][i] = dp[0][i - 1] + s2[i-1];
        }

        for (int i = 1; i <= n; i++)
        {
            for (int j = 1; j <= m; j++)
            {
                int take = 1e9;
                if (s1[i - 1] == s2[j - 1])
                {
                    take = dp[i - 1][j - 1];
                }
                int nottake = min(int(s1[i - 1]) + dp[i - 1][j], int(s2[j - 1]) + dp[i][j - 1]);

                dp[i][j] = min(take, nottake);
            }
        }

        return dp[n][m];
    }
};
