            int val2=(int)s2.charAt(i2);

            int res1=val1+helper(s1,s2,m,n,i1+1,i2,dp);
            int res2=val2+helper(s1,s2,m,n,i1,i2+1,dp);
            int res3=val1+val2+helper(s1,s2,m,n,i1+1,i2+1,dp);

            dp[i1][i2]=Math.min(res1,Math.min(res2,res2));
            return dp[i1][i2];
        }
    }
}
