class Solution {
    fun minimumDeleteSum(A: String, B: String): Int {
        var (M, N) = listOf(A.length, B.length)
        var ord = { x: Char -> x.toByte().toInt() }
        var dp = Array(M + 1) { IntArray(N + 1) { 0 } }
        for (i in M - 1 downTo 0) dp[i][N] = dp[i + 1][N] + ord(A[i])
        for (j in N - 1 downTo 0) dp[M][j] = dp[M][j + 1] + ord(B[j])
        for (i in M - 1 downTo 0) {
            for (j in N - 1 downTo 0) {
                var a = dp[i + 1][j + 1] + if (A[i] == B[j]) 0 else ord(A[i]) + ord(B[j])
                var b = dp[i + 1][j] + ord(A[i])
                var c = dp[i][j + 1] + ord(B[j])
                dp[i][j] = listOf(a, b, c).min()!!
            }
        }
        return dp[0][0]
    }
}
