    int longestCommonSubsequence(string text1, string text2) 
    {
        int s1 = text1.size(); int s2 = text2.size();
        int dp[s1+1][s2+1]; 

        for(int i=0; i<=s1; i++)
        {
            for(int j=0; j<=s2; j++)
            {
                if(i==0 || j==0)
                { dp[i][j] = 0; }
            }
        }
        for(int i=1; i<=s1; i++)
        {
            for(int j=1; j<=s2; j++)
            {
                if(text1[i-1] == text2[j-1])
                {
                    dp[i][j] = text1[i-1] + dp[i-1][j-1]; // slight variation in lcs(replaced 1 with text1[i-1] here)
                }
                else{
                    dp[i][j] = max(dp[i][j-1],dp[i-1][j]);
                }
            }
        }

        return dp[s1][s2] ;
    }
