class Solution:
    def minimumDeleteSum(self, s1: str, s2: str) -> int:

        l1 , l2 = len(s1) , len(s2)
        if l1 > l2 :
            self.minimumDeleteSum(s2,s1)

        dp2 = [0] * (l1 + 1)
        count2 = 0
        count1 = 0
        for i in range(1 , l2 + 1) :
            pre = dp2[0]
            count2 = count2 + ord(s2[i - 1]) #calculating ASCII sum for s2

            for j in range(1 , l1 + 1) :
                if i == 1:
                    count1 = count1 + ord(s1[j - 1]) #calculating ASCII sum for s1

                cur = dp2[j] #cur takes the role of dp[i - 1][j]

                if s2[i - 1] == s1[j - 1] :
                    dp2[j] = pre + ord(s2[i - 1])

                else:
                    dp2[j] = max(dp2[j - 1] , cur) #dp[j - 1] takes the role of dp[i][j - 1]

                pre = cur #pre takes the role of dp[i - 1][j - 1]

        LCS_sum = max(dp2)
        return count1 + count2 - 2 * LCS_sum
