class Solution {
public:

// this function fills up the 2D vector, that is memoization 

    int solve (string &a, string &b, int n, int m, vector<vector<int>> &dp ) {
      if (dp[n][m] != -1) 
		  return dp[n][m];
      if (n == 0 or m == 0) 
		  return 0;
      if (a[n-1] == b[m-1]) 
        return dp[n][m] = a[n-1] + solve(a, b, n-1, m-1, dp);
      else 
        return dp[n][m] = max(solve(a, b, n-1, m, dp), solve(a, b, n, m-1, dp));      
    }

    int findSize(vector<vector<int>> &dp, string &a, string &b) {
      int i = a.size(), j = b.size();
      string found = "";

      while (i > 0 and j > 0) {
        if (a[i-1] == b[j-1]) {
          found.push_back(a[i-1]);
          i--; j--;
        }
        else {
          if (dp[i-1][j] < dp[i][j-1])
              j--;
            else 
              i--;
          }
      }

// here we calculate the sum of ASCII values of all the characters in our subsequence
      int ans = 0;
      for (auto i : found)
        ans += i;

      return ans;
    }


    int minimumDeleteSum(string a, string b) {
      vector<vector<int>> dp (a.size()+1, vector<int>(b.size()+1, -1));
      solve(a, b, a.size(), b.size(), dp);
      int subseq = findSize(dp, a, b);

	  // total stores the sum of ASCII values of all the characters in both the strings
      int total = 0;
      for (auto i : a+b) 
        total += i; 
	  // subseq is subtracted twice because it occurs twice in the combined string a+b, once in a and again in b	
      return total - 2*subseq;
    }
};
