class Solution {
    public int minimumDeleteSum(String s1, String s2) {
        int l1 = s1.length(), l2 = s2.length();
        int[][] dp = new int[l1+1][l2+1];
        int totalAscii = 0;

        for(int i=1; i<=l1; i++) {
            int ch1 = s1.charAt(i-1);
            totalAscii += ch1;

            for(int j=1; j<=l2; j++) {
                int ch2 = s2.charAt(j-1);
                if (i == 1) totalAscii += ch2;

                if (ch1 == ch2) {
                    // Add the ascii value of character instead of +1.
                    dp[i][j] = dp[i-1][j-1] + ch1;
                }
                else  dp[i][j] = Math.max(dp[i-1][j], dp[i][j-1]);
            }
        }
        return totalAscii - dp[l1][l2] * 2;
    }
}
