            result = s2[l2] + recursive(s1,s2, l1, l2 + 1, cache);
        } else if (l2 >= s2.size()) {
            result = s1[l1] + recursive(s1, s2, l1 + 1, l2, cache);
        } else {
            result = (s1[l1] == s2[l2]) 
                    ? recursive(s1, s2, l1 + 1, l2 + 1, cache)
                    : min(s1[l1] + recursive(s1, s2, l1 + 1, l2, cache), s2[l2] + recursive(s1,s2, l1, l2 + 1, cache));
        }
        cache[l1][l2] = result;
        return result;
    }

    int minimumDeleteSum(string s1, string s2) {
        vector<vector<int>> cache(s1.size()+1, vector<int>(s2.size()+1, -1));
        return recursive(s1,s2,0,0,cache);
    }
};

